

public class boolean_example {
     public static void main(String[] args) {
          boolean initial=true;
          System.out.println("INITIAL BOOLEAN VALUE ");
          if(initial){
               System.out.println(" " +initial);
          }
          System.out.println("assining the condition to the boolean variable");
          initial=10<7;
          if(initial){
               System.out.println(" " +initial);
          }
          else {
               System.out.println("ASSINGED CONDITION FOR BOOLEAN EVALUTION IS  " + initial);
          }
     }
}
